""" Functional test in order to test the methode post_greeting_ascii"""import randomimport stringfrom ascii_data import post_greeting_asciilength_list = 15def generate_list(number):    """ generate a list of caracters with length of the list is the number in arg """    return [''.join(random.SystemRandom().choice(string.ascii_letters) for _ in range(1)) for x in range(number)]def main():    """ test the method  post_greeting_ascii"""    ret = generate_list(length_list)    print('list of alphabets:---test--->', ret)    res = post_greeting_ascii(**{"body": ret})    print('res:---ascii list--->', res[0])if __name__ == '__main__':    main()